import React,{useEffect,useState} from "react"
import CheckBox from "./CheckBox";
import "../styles/search.css"
import axios from 'axios';
import { baseURL } from "../utils/url";

function Search(props) {
    const [selectedValues,setSelectedValues] =useState({"Blu":0,"Bluetoot":0})
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [sub,setSub]= useState(false);
    // 
    const [result,setResults] = useState({"disease": "gregthtrhr","per": 0 , "test":"sxdcfvgbjhnmkl" })
    const [showCheckBoxes,setShowCheckBoxes] =useState(false)
    const handleCheckboxChange = (event) => {
        const { id, checked } = event.target;
        setSelectedValues((prevState) => ({
      ...prevState,
      [id]: checked ? 1 : 0, // set value to 1 if checked, 0 otherwise
    }));
      };
    console.log(selectedValues)


    //handlesubmit for transfering the data to the backend once the submit button clicked
    const handleSubmit = event => {
      event.preventDefault();
        // const checkboxArray = Object.values(selectedValues);

        // console.log(checkboxArray)
        submit = async() => {
          const response=await axios.post(`$`,
              selectedValues
          );
          console.log(response.data)
          setResults({"disease": response.ans ,"per":response.ans_prob, "test":response.test})
        }
          setSelectedValues({"Blu":0,"Bluetoot":0});
          setSub(true);
          setSearchResults([]);
          setShowCheckBoxes(false);


        // send checkboxArray using axios
      };

    const handleSearch = (event) => {
      setSearchTerm(event.target.value);
    };

    // const handleCheckbox = () => {
    //   setIsChecked(!isChecked);
    // };
    const handleSearchBarClick = ()=>{
        setShowCheckBoxes(!showCheckBoxes);
        console.log(showCheckBoxes);
    }


    useEffect(() => {
      // Here, you would typically make an API call to fetch the data for the checkboxes
      // For simplicity, we will create an array of items here
      const items = [
        { id: 1, value: "Blu" ,desc:"Desc1"},
        { id: 2, value: "Bluetoot", desc:"Desc2" },
        { id: 3, value: "Bluetooth",desc:"Desc3" },
        { id: 4, value: "Wifif",desc:"Desc4"},
        { id: 5, value: "Item 5",desc:"Desc5"},
      ];

      const results = items.filter((item) =>
        item.value.toLowerCase().includes(searchTerm.toLowerCase())
      );

      setSearchResults(results);
    }, [searchTerm]);

    return (
      <div className="container">
        <input
          className="searchbox"
          onClick={handleSearchBarClick}
          type="text"
          placeholder="Write down your symptoms..."
          value={searchTerm}
          onChange={handleSearch}
        />
        {searchResults.length >= 0 && (
          <div>
            <div className="container">
            {searchResults.map((item) => (
              (showCheckBoxes && (
                <div key={item.id}>
                    <CheckBox onCheckboxChange={handleCheckboxChange} label={item.value} disText={item.desc}/>
                </div>))
            ))}
            <button className="submitbtn" onClick={handleSubmit} >Submit</button>
            </div>
          </div>
        )}
        { sub===true ? <div>
          <br></br>
          <br></br>
          <div className="resultpop">
            <p>You have {result.per} persent of having {result.disease}</p>
            <p>It is advised to take the following tests as a precaution:- </p>
            <p>{result.test}</p>
          </div>
    </div> : <></>
    }
      </div>
    );
  }

  export default Search
