import React,{useEffect,useState} from "react"
import CheckBox from "./CheckBox";
import "../styles/search.css"
import axios from 'axios';

function Search(props) {
    const [selectedValues,setSelectedValues] = useState({'itching':0,'skin_rash':0,'nodal_skin_eruptions':0,'continuous_sneezing':0,'shivering':0,'chills':0,'joint_pain':0,'stomach_pain':0,'acidity':0,'ulcers_on_tongue':0,'muscle_wasting':0,'vomiting':0,'burning_micturition':0,'spotting_ urination':0,'fatigue':0,'weight_gain':0,'anxiety':0,'cold_hands_and_feets':0,'mood_swings':0,'weight_loss':0,'restlessness':0,'lethargy':0,'patches_in_throat':0,'irregular_sugar_level':0,'cough':0,'high_fever':0,'sunken_eyes':0,'breathlessness':0,'sweating':0,'dehydration':0,'indigestion':0,'headache':0,'yellowish_skin':0,'dark_urine':0,'nausea':0,'loss_of_appetite':0,'pain_behind_the_eyes':0,'back_pain':0,'constipation':0,'abdominal_pain':0,'diarrhoea':0,'mild_fever':0,'yellow_urine':0,'yellowing_of_eyes':0,'acute_liver_failure':0,'fluid_overload':0,'swelling_of_stomach':0,'swelled_lymph_nodes':0,'malaise':0,'blurred_and_distorted_vision':0,'phlegm':0,'throat_irritation':0,'redness_of_eyes':0,'sinus_pressure':0,'runny_nose':0,'congestion':0,'chest_pain':0,'weakness_in_limbs':0,'fast_heart_rate':0,'pain_during_bowel_movements':0,'pain_in_anal_region':0,'bloody_stool':0,'irritation_in_anus':0,'neck_pain':0,'dizziness':0,'cramps':0,'bruising':0,'obesity':0,'swollen_legs':0,'swollen_blood_vessels':0,'puffy_face_and_eyes':0,'enlarged_thyroid':0,'brittle_nails':0,'swollen_extremeties':0,'excessive_hunger':0,'extra_marital_contacts':0,'drying_and_tingling_lips':0,'slurred_speech':0,'knee_pain':0,'hip_joint_pain':0,'muscle_weakness':0,'stiff_neck':0,'swelling_joints':0,'movement_stiffness':0,'spinning_movements':0,'loss_of_balance':0,'unsteadiness':0,'weakness_of_one_body_side':0,'loss_of_smell':0,'bladder_discomfort':0,'foul_smell_of urine':0,'continuous_feel_of_urine':0,'passage_of_gases':0,'internal_itching':0,'toxic_look_(typhos)':0,'depression':0,'irritability':0,'muscle_pain':0,'altered_sensorium':0,'red_spots_over_body':0,'belly_pain':0,'abnormal_menstruation':0,'dischromic _patches':0,'watering_from_eyes':0,'increased_appetite':0,'polyuria':0,'family_history':0,'mucoid_sputum':0,'rusty_sputum':0,'lack_of_concentration':0,'visual_disturbances':0,'receiving_blood_transfusion':0,'receiving_unsterile_injections':0,'coma':0,'stomach_bleeding':0,'distention_of_abdomen':0,'history_of_alcohol_consumption':0,'fluid_overload.1':0,'blood_in_sputum':0,'prominent_veins_on_calf':0,'palpitations':0,'painful_walking':0,'pus_filled_pimples':0,'blackheads':0,'scurring':0,'skin_peeling':0,'silver_like_dusting':0,'small_dents_in_nails':0,'inflammatory_nails':0,'blister':0,'red_sore_around_nose':0,'yellow_crust_ooze':0,'prognosis':0})
    const [searchTerm, setSearchTerm] = useState("");
    const [searchResults, setSearchResults] = useState([]);
    const [sub,setSub]= useState(false);
    // 
    const [result,setResults] = useState({"disease": "gregthtrhr","per": 0 , "test":"sxdcfvgbjhnmkl" })
    const [showCheckBoxes,setShowCheckBoxes] =useState(false)
    const handleCheckboxChange = (event) => {
        const { id, checked } = event.target;
        setSelectedValues((prevState) => ({
      ...prevState,
      [id]: checked ? 1 : 0, // set value to 1 if checked, 0 otherwise
    }));
    };
    console.log(selectedValues)

    const submit = async() => {
      const response=await axios.post('/',
          selectedValues
      );
      console.log(response.data)
      setResults({"disease": response.ans ,"per":response.ans_prob, "test":response.test})
    }
    //handlesubmit for transfering the data to the backend once the submit button clicked
    const handleSubmit = event => {
      event.preventDefault();
        // const checkboxArray = Object.values(selectedValues);

        // console.log(checkboxArray)
          submit();
          setSelectedValues({"Blu":0,"Bluetoot":0});
          setSub(true);
          setSearchResults([]);


        // send checkboxArray using axios
      };

    const handleSearch = (event) => {
      setSearchTerm(event.target.value);
    };

    // const handleCheckbox = () => {
    //   setIsChecked(!isChecked);
    // };
    const handleSearchBarClick = ()=>{
        setShowCheckBoxes(!showCheckBoxes);
        console.log(showCheckBoxes)
    }


    useEffect(() => {
      // Here, you would typically make an API call to fetch the data for the checkboxes
      // For simplicity, we will create an array of items here
      const items = [
        { id: 1, value: "Blu" ,desc:"Desc1"},
        { id: 2, value: "Bluetoot", desc:"Desc2" },
        { id: 3, value: "Bluetooth",desc:"Desc3" },
        { id: 4, value: "Wifif",desc:"Desc4"},
        { id: 5, value: "Item 5",desc:"Desc5"},
      ];

      const results = items.filter((item) =>
        item.value.toLowerCase().includes(searchTerm.toLowerCase())
      );

      setSearchResults(results);
    }, [searchTerm]);

    return (
      <div className="container">
        <input
          className="searchbox"
          onClick={handleSearchBarClick}
          type="text"
          placeholder="Write down your symptoms..."
          value={searchTerm}
          onChange={handleSearch}
        />
        {searchResults.length >= 0 && (
          <div>
            <div className="container">
            {searchResults.map((item) => (
              (showCheckBoxes && (
                <div key={item.id}>
                    <CheckBox onCheckboxChange={handleCheckboxChange} label={item.value} disText={item.desc}/>
                </div>))
            ))}
            <button className="submitbtn" onClick={handleSubmit} >Submit</button>
            </div>
          </div>
        )}
        { sub===true ? <div>
          <br></br>
          <br></br>
          <div className="resultpop">
            <p>You have {result.per} persent of having {result.disease}</p>
            <p>It is advised to take the following tests as a precaution:- </p>
            <p>{result.test}</p>
          </div>
    </div> : <></>
    }
      </div>
    );
  }

  export default Search
